function parseSou(htmlContent){
    const regex = /<a\s+href=([^>]*)>/g;
    let matches = [];
    let match;
    while ((match = regex.exec(htmlContent)) !== null) {
        if (match[1] === "/" || match[1] === "https://t.me/xiaoyaliu"){
            continue;
        }
        matches.push(match[1]);
    }
    return matches;
}

